program.vs:
precision highp float;
attribute vec3 gPoint;
attribute vec2 gTexcoord;
uniform mat4 gWVP;
varying vec2 mTexcoord;
void main()
{
  gl_Position = gWVP * vec4(gPoint, 1);
  mTexcoord = gTexcoord;
}

program.fs:
precision highp float;
uniform sampler2D gBaseTex;
varying vec2 mTexcoord;
void main()
{
  gl_FragColor = texture2D(gBaseTex, mTexcoord);
}
